  filteredItems = [...this.items];

  constructor() {
    this.categories.forEach(category => {
      this.selectedCategories[category] = false;
    });
  }

  ngOnInit(): void {
    this.filterItems();
  }

  filterItems(): void {
    this.filteredItems = this.items.filter(item => {
      const matchesSearch = item.name.toLowerCase().includes(this.searchTerm.toLowerCase());
      const matchesCategory = Object.keys(this.selectedCategories)
        .filter(category => this.selectedCategories[category])
        .includes(item.category);

      return matchesSearch && (Object.values(this.selectedCategories).includes(true) ? matchesCategory : true);
    });
  }

  onSearch(): void {
    this.filterItems();
  }

  onCategoryChange(): void {
    this.filterItems();
  }

  sortItems(by: string): void {
    if (by === 'name') {
      this.filteredItems.sort((a, b) => a.name.localeCompare(b.name));
    }
  }
  openSidebar() {
    this.sidenav.open();
  }

  onPageChange(event: PageEvent) {
    console.log("Page Changed: ", event);  // Debugging
    this.pageSize = event.pageSize;
    this.pageIndex = event.pageIndex;
     const startIndex = this.pageIndex * this.pageSize;
    return this.filteredItems.slice(startIndex, startIndex + this.pageSize);
  }
